import React from 'react';
import './App.css';

class Editor extends React.Component
{
	constructor(props)
	{
		super(props);

		this.runParentHandleChange = this.runParentHandleChange.bind(this);
	}

	runParentHandleChange(e)
	{
		this.props.handleChange(e);
	}
	render()
	{
		return (
			<div>
				<h1>Editor</h1>
				<textarea onChange={this.runParentHandleChange}></textarea>
			</div>
		);
	}
}

class Previewer extends React.Component
{
	render()
	{
		return (
			<div>
				<h1>Previewer</h1>
				<div>{this.props.text}</div>
			</div>
		);
	}
}

class App extends React.Component
{
	constructor(props)
	{
		super(props);
		this.state = {
			'showMeWhatYouGot': ''
		};
		this.handleChange = this.handleChange.bind(this);
	}

	handleChange(e)
	{
		e.preventDefault();
		this.setState({showMeWhatYouGot: marked(e.target.value)});
	}

	render()
	{
		return (
			<div className="App">
				<Editor handleChange={this.handleChange} />
				<Previewer text={this.state.showMeWhatYouGot}/>
			</div>
		);
	}
}

export default App;
