import React from 'react';
import './App.css';

class Editor extends React.Component
{
	render()
	{
		return (
			<div>
				<h1>Editor</h1>
				<textarea></textarea>
			</div>
		);
	}
}

class Previewer extends React.Component
{
	render()
	{
		return (
			<div>
				<h1>Previewer</h1>
				<div>{this.props.showMeWhatYouGot}</div>
			</div>
		);
	}
}

class App extends React.Component
{
	constructor(props)
	{
		super(props);
		this.state = {
			'showMeWhatYouGot': ''
		};
	}

	render()
	{
		return (
			<div className="App">
				<Editor />
				<Previewer />
			</div>
		);
	}
}

export default App;
