import React from 'react';
import './App.css';

class Editor extends React.Component
{
	runParentHandleChange(e)
	{
		this.props.handleChange(e);
	}
	render()
	{
		return (
			<div>
				<h1>Editor</h1>
				<textarea onChange={this.runParentHandleChange}></textarea>
			</div>
		);
	}
}

class Previewer extends React.Component
{
	render()
	{
		return (
			<div>
				<h1>Previewer</h1>
				<div>{this.props.showMeWhatYouGot}</div>
			</div>
		);
	}
}

class App extends React.Component
{
	constructor(props)
	{
		super(props);
		this.state = {
			'showMeWhatYouGot': ''
		};
	}

	handleChange(e)
	{
		e.preventDefault();
		this.setState({showMeWhatYouGot: e.target.value});
	}

	render()
	{
		return (
			<div className="App">
				<Editor />
				<Previewer />
			</div>
		);
	}
}

export default App;
